--
-- Name: v_event_data; Type: VIEW; Schema: public; Owner: unep
--

CREATE VIEW public.v_event_data AS
 SELECT e.id,
    e.title,
    e.start_date,
    e.end_date,
    e.description,
    e.image,
    e.geo_coverage_type,
    e.remarks,
    e.created,
    e.modified,
    e.country,
    e.city,
    e.reviewed_at,
    e.reviewed_by,
    e.review_status,
    geo.geo_coverage_values,
    lang.languages,
    tag.tags
   FROM (((public.event e
     LEFT JOIN ( SELECT elu.event,
            json_agg(json_build_object('url', elu.url, 'iso_code', l.iso_code)) AS languages
           FROM (public.event_language_url elu
             JOIN public.language l ON ((elu.language = l.id)))
          GROUP BY elu.event) lang ON ((e.id = lang.event)))
     LEFT JOIN ( SELECT et.event,
            json_agg(t.tag) AS tags
           FROM (public.event_tag et
             JOIN public.tag t ON ((et.tag = t.id)))
          GROUP BY et.event) tag ON ((e.id = tag.event)))
     LEFT JOIN ( SELECT eg.event,
            json_agg(COALESCE(c.iso_code, (cg.name)::bpchar)) AS geo_coverage_values
           FROM ((public.event_geo_coverage eg
             LEFT JOIN public.country c ON ((eg.country = c.id)))
             LEFT JOIN public.country_group cg ON ((eg.country_group = cg.id)))
          GROUP BY eg.event) geo ON ((e.id = geo.event)))
  ORDER BY e.created;


ALTER TABLE public.v_event_data OWNER TO unep;

--
-- Name: v_event_geo; Type: VIEW; Schema: public; Owner: unep
--

CREATE VIEW public.v_event_geo AS
 SELECT e.id,
    country.iso_code AS geo_coverage_iso_code
   FROM (((public.event e
     LEFT JOIN public.event_geo_coverage geo ON ((e.id = geo.event)))
     LEFT JOIN public.country_group_country cgc ON ((geo.country_group = cgc.country_group)))
     LEFT JOIN public.country ON ((cgc.country = country.id)))
  WHERE ((e.geo_coverage_type = 'regional'::public.geo_coverage_type) OR (e.geo_coverage_type = 'global with elements in specific areas'::public.geo_coverage_type))
UNION ALL
 SELECT e.id,
    country.iso_code AS geo_coverage_iso_code
