-- Name: v_technology_geo; Type: VIEW; Schema: public; Owner: unep
--

CREATE VIEW public.v_technology_geo AS
 SELECT t.id,
    country.iso_code AS geo_coverage_iso_code
   FROM (((public.technology t
     LEFT JOIN public.technology_geo_coverage geo ON ((t.id = geo.technology)))
     LEFT JOIN public.country_group_country cgc ON ((geo.country_group = cgc.country_group)))
     LEFT JOIN public.country ON ((cgc.country = country.id)))
  WHERE ((t.geo_coverage_type = 'regional'::public.geo_coverage_type) OR (t.geo_coverage_type = 'global with elements in specific areas'::public.geo_coverage_type))
UNION ALL
 SELECT t.id,
    country.iso_code AS geo_coverage_iso_code
   FROM ((public.technology t
     LEFT JOIN public.technology_geo_coverage geo ON ((t.id = geo.technology)))
     LEFT JOIN public.country ON ((geo.country = country.id)))
  WHERE ((t.geo_coverage_type = 'national'::public.geo_coverage_type) OR (t.geo_coverage_type = 'transnational'::public.geo_coverage_type) OR (t.geo_coverage_type = 'sub-national'::public.geo_coverage_type))
UNION ALL
 SELECT t.id,
    '***'::bpchar AS geo_coverage_iso_code
   FROM public.technology t
  WHERE (t.geo_coverage_type = 'global'::public.geo_coverage_type);


ALTER TABLE public.v_technology_geo OWNER TO unep;

--
-- Name: v_technology_search_text; Type: VIEW; Schema: public; Owner: unep
--

CREATE VIEW public.v_technology_search_text AS
 SELECT technology.id,
    to_tsvector('english'::regconfig, COALESCE(technology.name, ''::text)) AS search_text
   FROM public.technology
  ORDER BY technology.created;


ALTER TABLE public.v_technology_search_text OWNER TO unep;

--
-- Name: v_technology; Type: VIEW; Schema: public; Owner: unep
--

CREATE VIEW public.v_technology AS
 SELECT 'technology'::text AS topic,
    geo.geo_coverage_iso_code,
    st.search_text,
    row_to_json(t.*) AS json
   FROM ((public.v_technology_data t
     LEFT JOIN public.v_technology_geo geo ON ((t.id = geo.id)))
     LEFT JOIN public.v_technology_search_text st ON ((t.id = st.id)))
  ORDER BY t.created;


ALTER TABLE public.v_technology OWNER TO unep;

--
-- Name: v_topic; Type: VIEW; Schema: public; Owner: unep
--

CREATE VIEW public.v_topic AS
 SELECT v_event.topic,
    v_event.geo_coverage_iso_code,
    v_event.search_text,
    v_event.json
   FROM public.v_event
UNION ALL
 SELECT v_policy.topic,
    v_policy.geo_coverage_iso_code,
    v_policy.search_text,
    v_policy.json
   FROM public.v_policy
UNION ALL
 SELECT v_resource.topic,
    v_resource.geo_coverage_iso_code,
    v_resource.search_text,
    v_resource.json
   FROM public.v_resource
UNION ALL
 SELECT v_technology.topic,
    v_technology.geo_coverage_iso_code,
    v_technology.search_text,
    v_technology.json
   FROM public.v_technology
UNION ALL
 SELECT v_project.topic,
    v_project.geo_coverage_iso_code,
    v_project.search_text,
    v_project.json
   FROM public.v_project
UNION ALL
 SELECT v_stakeholder.topic,
    v_stakeholder.geo_coverage_iso_code,
    v_stakeholder.search_text,
    v_stakeholder.json
   FROM public.v_stakeholder
UNION ALL
 SELECT v_organisation.topic,
    v_organisation.geo_coverage_iso_code,
